CREATE TABLE AUCTION (
  AUCTION_ID INT PRIMARY KEY,
  CARGO_ID INT,
  OWNER_ID INT,
  TRUCKER_ID INT,
  AUCTION_EXPIRE_DATE DATE,
  AUCTION_START_DATE DATE,
  TRANSPORT_FEE_UPPER_LIMIT INT,
  DETERMINED_TUCKER_ID INT,
  AUCTION_STATUS VARCHAR(255),
  FOREIGN KEY(CARGO_ID) REFERENCES CARGO(CARGO_ID) ON DELETE CASCADE,
  FOREIGN KEY(OWNER_ID) REFERENCES OWNER(OWNER_ID) ON DELETE CASCADE,
  FOREIGN KEY(TRUCKER_ID) REFERENCES TRUCKER(TRUCKER_ID) ON DELETE CASCADE
)